/*Table structure for table `article` */

DROP TABLE IF EXISTS `article`;

CREATE TABLE `article` (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT '帖子 id',
  `title` varchar(512) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci DEFAULT NULL COMMENT '帖子标题',
  `type` varchar(64) DEFAULT NULL COMMENT '类型',
  `content` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci COMMENT '内容',
  `user_id` bigint DEFAULT NULL COMMENT '帖子的用户 id',
  `gmt_create` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '创建日期',
  `gmt_modified` datetime DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '修改日期',
  `is_deleted` tinyint DEFAULT '0' COMMENT '逻辑删除',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

/*Data for the table `article` */

insert  into `article`(`id`,`title`,`type`,`content`,`user_id`,`gmt_create`,`gmt_modified`,`is_deleted`) values (1,'空白期青花瓷是指什么？','test','明朝的正统、景泰、天顺，虽然只有不到30年的短短时间，但是经历了外族入侵、兄弟相争等复杂的历史事件。战乱频发，民不聊生，致使中国的瓷器业并没有大力发展。这段时期青花瓷器的烧造记录比较少，而且传世器物并不多见，因此，这段时期也称之为制瓷业的黑暗期。但是经过专家学者对这三朝青花瓷器的深入研究，却发现这三朝并非一片空白，除了有民窑瓷器，还有官窑瓷器。青花瓷器并没有断烧，只是数量上有所减少。',4,'2023-03-24 00:41:16','2023-03-24 00:41:16',0),(2,'什么是暗八仙？','test','暗八仙是指瓷器的宗教纹式之一，源于八仙纹，以道教中八位仙人所持之物为代表，而八位仙人并不出现。扇子代表仙人汉钟离，笛子代表仙人韩湘子，荷花代表仙人何仙姑，宝剑代表仙人吕洞宾，阴阳板代表仙人曹国舅，渔鼓代表仙人张家老，花蓝代表仙人蓝采和、葫芦和拐杖代表仙人铁拐李。',4,'2023-03-24 00:41:46','2023-03-24 00:41:46',0),(3,'什么是开光？','test','开光也称之为“开窗”，是在器物的显著部位以流畅的线条勾勒出多种式样的框，框内可以绘制出各种图案，起到突出主题纹饰的作用。这些各式各样的框常见的包括有圆形、菱形、方形、扇面形等。',1,'2023-03-24 00:43:20','2023-03-24 00:43:20',0),(4,'玻璃白是什么？','test','玻璃白是一种绘制粉彩瓷器时要使用到的颜料。玻璃白并不透明，可以使粉彩的颜色更加柔和、明亮，具有粉质感。',2,'2023-03-24 00:45:10','2023-03-24 00:45:10',0),(5,'古陶瓷鉴赏的常见物理小知识','test','中国陶瓷的发展历史源远流长，凝结积聚了中华文化之精髓。郭沫若先生曾经评价说：“中国古代陶瓷发展的历史，就是中华民族的发展历史，它是几千年来中华文化历史的见证。”一件古陶瓷本身就集合了化学、物理、历史、考古、美术、文化、宗教、工艺、民俗等等多种领域，这些可能也就是古陶瓷的魅力所在。也正是这些时间文化的魅力，给古陶瓷蒙上了神秘的面纱。近些年的影视剧作品与小说故事，通过一些夸大的手法，表现了一些古代陶瓷的欣赏与鉴赏方式。虽然有趣，但是也往往让感兴趣的朋友看得有些云里雾里。',2,'2023-03-24 00:45:42','2023-03-24 00:45:42',0);

/*Table structure for table `comment` */

DROP TABLE IF EXISTS `comment`;

CREATE TABLE `comment` (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT '评论 id',
  `article_id` bigint DEFAULT NULL COMMENT '帖子 id',
  `parent_id` bigint DEFAULT NULL COMMENT '父评论 id',
  `user_id` bigint DEFAULT NULL COMMENT '用户 id',
  `content` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci COMMENT '评论内容',
  `gmt_create` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '创建日期',
  `gmt_modified` datetime DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '修改日期',
  `is_deleted` tinyint DEFAULT '0' COMMENT '逻辑删除',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=11 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

