/*Table structure for table `porcelain` */

DROP TABLE IF EXISTS `porcelain`;

CREATE TABLE `porcelain` (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT '瓷器 id',
  `title` varchar(512) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci DEFAULT NULL COMMENT '瓷器标题',
  `type` varchar(64) DEFAULT NULL COMMENT '类型',
  `dynasty` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci DEFAULT NULL COMMENT '朝代',
  `url` varchar(1024) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci DEFAULT NULL COMMENT '相关图片',
  `introduction` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci COMMENT '介绍',
  `detail` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci COMMENT '细节描述',
  `gmt_create` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '创建日期',
  `gmt_modified` datetime DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '修改日期',
  `is_deleted` tinyint DEFAULT '0' COMMENT '逻辑删除',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=78 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

/*Data for the table `porcelain` */

insert  into `porcelain`(`id`,`title`,`type`,`dynasty`,`url`,`introduction`,`detail`,`gmt_create`,`gmt_modified`,`is_deleted`) values (1,'宋吉州窑玳瑁釉罐','test','宋','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end/4b3b7f3381ff4c5ab6e314b6027c3406.jpeg','吉州窑，址位于江西吉安地区，古代称之为吉州，因此得名于吉州窑。吉州窑的瓷器具有浓厚的地方风格与艺术特色。江西地区后来又出现了闻名世界的瓷都景德镇，因此吉州窑的瓷器烧造，对后来景德镇瓷器的发展，奠定了良好的基础。吉州窑的烧制始于唐代末年，在宋代达到鼎盛。北宋时期，吉州窑有了长足的发展。伴随着北宋的灭亡，大量北方的工匠迁移到南方，使得南方的瓷器烧到呈现了更加兴盛的局面，吉州窑在南宋时期达到了艺术上的顶峰。','高11.1厘米，口径12.1厘米，足径3.1厘米','2023-03-08 01:13:31','2023-03-14 16:43:26',0),(6,'南宋龙泉窑青釉菱口瓜棱瓷瓶','瓷瓶','宋','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end/fffedc7b174f419b86e8305ab85fca8b.jpeg','撇口，长颈，长圆腹，花瓣式圈足外撇，通体有瓜棱形竖纹。白胎，施粉青釉。这件青釉瓜棱瓶为仿官窑器，因瓶腹由凹凸的弧线组成反棱似的形体而得名。釉色温润明亮，造型生动又不失沉静优雅，体现了文人情趣与民间趣味的融合，是一件雅俗共赏的工艺美术作品。','高16厘米。','2023-03-08 02:03:36','2023-03-27 08:11:30',0),(7,'青花梅瓶','梅瓶','元','http://www.artwaker.com/assets/static/upload/image/2020/03/30/a850cb148b6c7f197b89be9aa7cdd8f3.jpg','梅瓶最早出现于唐代，宋辽时期较为流行，并且出现了许多新品种。宋元时期各地瓷窑均有烧制，以元代景德镇青花梅瓶最为精湛。近代许之衡在《饮流斋说瓷》一书中详细地描述了梅瓶的形制、特征及名称由来：“梅瓶口细而颈短，肩极宽博，至胫稍狭，抵于足微丰，口径之小仅与梅之瘦骨相称，故名梅瓶。”关于梅瓶的用途，磁州窑系有一对为白地黑花梅瓶，瓶身一书“清沽美酒”，一书“醉乡酒海”，作盛酒的用具是明白无疑的。梅瓶既是酒器，又是一件令人爱不释手的观赏品。因此，这类器多制作精美，不但考虑到贮酒容量，还要注意造型优美。','小口、短颈、丰肩，瘦底、圈足的瓶式，以口小只能插梅枝而得名。因瓶体修长，宋时称为“经瓶”，作盛酒用器，造型挺秀、俏丽，明朝以后被称为梅瓶。','2023-03-14 14:33:58','2023-03-14 16:43:33',0),(8,'玉壶春瓶','春瓶','清','http://www.artwaker.com/assets/static/upload/image/2020/03/30/726dcfbd553b26f464338dade82d3308.jpg','由诗句“玉壶先春”得名，颈较细，颈部中央微微收束，颈部向下逐渐加宽过渡为杏圆状下垂腹，曲线变化圆缓。圈足相对较大，或内敛或外撇。是由唐代寺院里的净水瓶演变而来。基本形制为撇口、细颈、圆腹、圈足。玉壶春瓶的造型定型于北宋时期，在当时是一种装酒的实用器具，后来逐渐演变为观赏性的陈设瓷，这种瓶的造型定窑、耀州窑、磁州窑、龙泉窑、景德镇均有烧造。历经宋、元、明、清、民国直至现代，成为中国瓷器造型中的一种典型器物，品种有青花、釉里红、五彩、斗彩、粉彩等。','撇口、细颈、垂腹、圈足。','2023-03-14 14:43:11','2023-03-14 14:47:24',0),(9,'洋彩花卉纹蒜头瓶','蒜头瓶','清','http://www.artwaker.com/assets/static/upload/image/2020/03/30/fa137a0d6570e0ede44ac68322c24598.jpg','明清时期景德镇窑常见的一种瓶式之一，仿自汉代青铜蒜头壶。状如蒜头而颈略长，亦可称之为虎爪瓶。瓷制蒜头瓶始烧于宋代，流行于明清，以景德镇窑制品为多见，品种有青花、五彩、洒蓝描金等。明清景德镇窑蒜头瓶有青花、五彩、粉彩、以及蓝釉、酱釉等各种单色釉品种。明嘉靖蒜头瓶是肩部有堆塑蟠螭装饰。明成化蒜头瓶，仅口部作花瓣状，耳为随意堆塑。明正德蒜头瓶，在瓶腹上设出戟。明万历蒜头瓶，趋于简化，口部已无蒜头了，但器形高大，以青花五彩为多见。清代蒜头瓶的器形趋于轻盈秀美。','长颈，削肩，圆腹，圈足，瓶口似蒜头','2023-03-14 14:47:09','2023-03-14 16:43:46',0),(10,'天蓝釉胆式瓶','胆式瓶','清','http://www.artwaker.com/assets/static/upload/image/2020/03/30/0cdbed83bc9611e01a11b171a1fa8e5c.jpg','瓶的一种式样。宋代哥窑、钧窑烧造此式，清代景德镇窑多见，大多为单色釉。葫芦，故名。南宋龙泉窑创制，元、明景德镇窑多见，并有四方、六方、八方、上圆下方及扁腹等式。品种有青花、五彩、白釉、黄釉等，有的书“大吉”二字。','直口，长颈，削肩，腹下部丰满，器形似胆','2023-03-14 16:27:39','2023-03-14 16:45:53',0),(11,'黄釉刻荷莲纹双耳宝月瓶','宝月瓶','宋','http://www.artwaker.com/assets/static/upload/image/2020/03/30/c546da8c78914cbf8354eb95f6f5df33.jpg','清代的一种瓷器造型，亦称“抱月瓶”。明代永乐、宣德景德镇窑常见器物之一，雍正早期烧制，后停烧。乾隆时又有烧制，多为青花。','小口、直颈，颈侧双耳，因瓶腹似圆月。','2023-03-14 16:32:39','2023-03-27 08:11:33',0),(12,'珐琅彩题诗「寻隐者不遇」摇铃尊','摇铃尊','清','http://www.artwaker.com/assets/static/upload/image/2020/03/30/bca2a1c288bb9a0f0d1190ea7e12712d.jpg','是清康熙年间的流行器式。以青花釉里红制品为多。由于此类器型罕见，存世量稀少，历来身价不菲，备受关注。','小口，细长颈，丰肩，弧形或筒式腹，浅圈足，平底，形似带把的摇铃。','2023-03-14 16:36:05','2023-03-14 16:45:55',0),(13,'天蓝釉贯耳折方瓶','贯耳瓶','清','http://www.artwaker.com/assets/static/upload/image/2020/03/30/057d54fc2b11f95a31fb7ac8990b76b0.jpg','流行于宋代。仿汉代投壶式样。哥窑、官窑、龙泉窑等多有烧制，清代亦有仿制。','直颈较长，腹部扁圆，圈足，颈部两侧对称且贴着竖直的管状贯耳。','2023-03-14 16:37:33','2023-03-14 16:45:57',0),(14,'青花缠枝莲纹赏瓶','赏瓶','清','http://www.artwaker.com/assets/static/upload/image/2020/03/30/03d26928dece7d5b75f80e0e5480ea54.jpg','中国陶瓷艺术的珍品。为雍正一朝最新出现的一种造型，作赏赐之用。其器型来源于玉壶春瓶，传世品形制基本相同。','撇口，细长颈，肩部装饰凸弦纹，圆腹，圈足。','2023-03-14 16:38:38','2023-03-14 16:45:59',0),(15,'斗彩花蝶纹灯笼瓶','灯笼瓶','清','http://www.artwaker.com/assets/static/upload/image/2020/03/30/11831d290d748728055cbff4788a96fb.jpg','因为形似灯笼而得名。灯笼瓶是雍正时期景德镇官窑创烧，流行于清雍正、乾隆时期。乾隆时期灯笼瓶的制作较多。','器呈直口，短颈，丰肩，筒腹，圈足。','2023-03-14 16:39:52','2023-03-14 16:46:01',0),(16,'粉彩六桃图天球瓶','天球瓶','清','http://www.artwaker.com/assets/static/upload/image/2020/03/30/8e084acbe72eb6d3b56e65622c5fce17.jpg','天球瓶是受西亚文化影响极深的一种瓷器造型，创烧于明代永乐、宣德年间的景德镇窑，多见青花，以海水龙纹为贵。永乐朝开始成为观赏式瓶，宣德年间较为流行。宣德天球瓶胎骨厚重，敛口短颈，上阔下敛；腹大而扁，砂底微凹。后仿的器型多颈长腹圆。清代天球瓶颈稍长、腹较圆，康熙时为平砂底，雍正为直口微撇，腹扁圆，乾隆时天球瓶直口较粗，圆腹，假圈足，部分为釉底。除了传统的青花品种外，五彩、粉彩等彩绘天球瓶出现。','瓶形为小口、直颈、丰肩、假圈足、砂底微凹。因圆球腹硕大，像是从天降下来似的。','2023-03-14 16:42:10','2023-03-14 16:46:09',0),(18,'龙泉窑蟠龙瓶','蟠龙瓶','宋','http://www.artwaker.com/assets/static/upload/image/2020/03/30/2088c2bf175cdd6e28f775bb40a2dd26.jpg','蟠龙瓶初为明器。因瓶的颈、肩处堆塑一条蟠龙，故名。亦称“扳依瓶”或“招魂瓶”，常见于宋、元明各代。后来，蟠龙装饰也常见于各式瓶型中，也将这种瓶型称为“蟠龙瓶”。','长颈，有盖，盖顶钮塑成虎、凤、鹤等各种形式，圆腹、圈足。','2023-03-14 16:45:47','2023-03-27 08:11:36',0),(19,'龙泉窑青瓷多管瓶','多管瓶','宋','http://www.artwaker.com/assets/static/upload/image/2020/03/30/f525f2d1da5bb998685e509300b27e63.jpg','因器身肩部塑有向上直立的管，故名。直口，上有花形钮盖，瓶身有圆筒式和多节葫芦式。宋代南北瓷窑均有烧造，以龙泉窑制品较多，一般为五管。北方磁州窑系也烧制。','瓶体短而浑圆，肩部有直立六管，管粗短。','2023-03-14 16:47:28','2023-03-27 08:11:40',0),(20,'白瓷花口瓶','花口瓶','夏','http://www.artwaker.com/assets/static/upload/image/2020/03/30/f5e46aebe5256ae2f9657f3082a0acda.jpg','宋代景德镇、磁州窑、耀州窑均有烧造，明清亦仿制。','细颈微撇，圆腹，撇足，瓶口有如开放的花瓣。','2023-03-14 16:48:37','2023-03-27 08:14:21',0),(21,'官窑琮式瓶','琮式瓶','宋','http://www.artwaker.com/assets/static/upload/image/2020/03/30/147ae80d3cab82a2d944212413380a53.jpg','仿周代礼器玉琮烧制。宋代创制，以龙泉窑、官窑多见，元、明清仍有烧制。','圆口、方身、圈足，器形四面以凸起的线纹为饰，口与足大小相若。','2023-03-14 16:49:28','2023-03-27 08:14:08',0),(22,'青花三友一统瓶','象腿瓶','清','http://www.artwaker.com/assets/static/upload/image/2020/03/30/9c90254ff6a41d5637422eb0035e63d3.jpg','又名一统瓶，清代创烧的一种瓷器造型。因“筒”与“统”谐音，有“大清天下一统”的寓意。多见于清初顺治、康熙两朝，品种有青花、五彩。','瓶形为广口外撇，短颈，肩部宽与口相若，身如直筒状，平底，造型如粗壮的象腿。','2023-03-14 16:51:10','2023-03-14 16:51:10',0),(23,'官窑粉彩蝠桃橄榄瓶','橄榄瓶','清','http://www.artwaker.com/assets/static/upload/image/2020/03/30/2e875088984f71ddab66cd092792937f.jpg','瓶体似橄榄，故名。宋代河南登封窑创制，清代仍有烧造，造型略有变化，颈部稍长，匀称秀美。','撇口，短颈，肩、颈部内收，腹部微鼓，足部外撇，口、底大小相若。','2023-03-14 16:52:19','2023-03-14 16:52:19',0),(24,'青花花卉凤尾尊','凤尾瓶','清','http://www.artwaker.com/assets/static/upload/image/2020/03/30/1713e86b3252efa09938e383c68500b3.jpg','清代康熙景德镇窑创制，产品多为民窑，品种有青花、五彩等。','喇叭状口，长颈，鼓腹，下敛，至底又广，形状略似凤尾。','2023-03-14 16:53:06','2023-03-14 16:53:06',0),(25,'洒蓝釉描金花鸟棒槌瓶','棒槌瓶','清','http://www.artwaker.com/assets/static/upload/image/2020/03/30/b3f545f4101d5016cdafd6cc39a8ee51.jpg','分为硬棒槌瓶，方棒槌瓶和软棒槌瓶。硬棒槌瓶又称圆棒槌瓶。器腹有高矮和粗细之分，粗腹者，器形庄重，细腹者，器形秀丽。','盘口，短直颈，圆折肩，圆筒状长腹，圈足，底部多为平切式二层台。','2023-03-14 16:54:09','2023-03-14 16:54:09',0),(26,'红彩描金开光走兽花鸟纹油锤瓶','油锤瓶','清','http://www.artwaker.com/assets/static/upload/image/2020/03/30/d7d160790ab94011fd70cb1811c36342.jpg','清代康熙景德镇窑创制，品种以青花和颜色釉多见。','小口，细长颈，圆腹，圈足，形似榨油作坊用的油锤。','2023-03-14 16:54:59','2023-03-14 16:54:59',0),(27,'淡黄釉柳叶瓶','柳叶瓶','清','http://www.artwaker.com/assets/static/upload/image/2020/03/30/40d2c72bb529da7b629820d664933bcc.jpg','清代康熙景德镇窑创制，康熙豇豆红釉柳叶瓶，是极为珍贵的产品。','撇口，短颈，丰肩，肩下削瘦至足，足内凹，器身细长，形似柳叶。','2023-03-14 16:55:36','2023-03-14 16:55:36',0),(28,'官窑矾红彩藏草瓶','藏草瓶','清','http://www.artwaker.com/assets/static/upload/image/2020/03/30/eb05598d662c49288292cea0dc189cc9.jpg','清代雍正景德镇窑创制，系宫廷用器，乾隆以后不见。','小口，直颈，颈中间凸出，鼓腹，腹一侧有流，腹下承以喇叭形足，造型奇特。','2023-03-14 16:56:14','2023-03-14 16:56:14',0),(29,'粉彩暗八仙纹双耳转心瓶','转心瓶','清','http://www.artwaker.com/assets/static/upload/image/2020/03/30/18c1038d437a0ae5a8666fa8e0740ae2.jpg','在一个镂孔瓶内，套装一个可以转动的内瓶，上绘各种纹样，拨动上瓶瓶口，转动内瓶时，通过外瓶的镂孔，可以看见不同的画面，故称转心瓶。清代乾隆景德镇窑创制，多为官窑器。','','2023-03-14 16:57:19','2023-03-14 16:57:19',0),(30,'定窑白釉刻花莲瓣纹龙首净瓶','净瓶','宋','http://www.artwaker.com/assets/static/upload/image/2020/03/30/514f91e39cdad3d41dd6fcd5be82d6b4.jpg','净瓶（梵名 kalasa），指以陶或金属等制造，用以容水的器具，为比丘十八物之一，盛水供饮用或洗濯，又称水瓶或澡瓶。《千手千眼观世音菩萨大悲心陀罗尼》以其为千手观音四十手持物之一。','','2023-03-14 16:58:09','2023-03-27 08:14:24',0),(31,'西夏白釉剔刻纹二系扁壶','扁壶','夏','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end/21e468d465ff42e786a0155ac825b907.png','小口卷沿，短颈，肩部有对称耳系，边缘有合缝泥条堆纹。腹、背部中央有对称圈足，其中正面腹部圈足均上白釉，背面腹部圈足口沿露胎未上釉，腹、背主体图案各剔刻三朵缠技牡丹花。白釉剔花是将花纹外的空间或花纹本身的化妆土剔去，露出胎色，再施以透明釉烧制而成，由于其胎细质高，在西夏瓷器中堪称上品，存世量极少。','器高28cm口径2.5cm腹径25cm底圈足径9cm','2023-03-27 08:20:03','2023-03-27 08:20:03',0),(32,'黑釉剔刻花瓷扁壶','扁壶','夏','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end/ccf0008bd36d479c8a683868e78178b4.png','为了方便携带，在扁腹肩部加有对称双耳。器腹饰剔地刻花的开光折枝牡丹花纹，其外刻画花叶和弧线纹，纹样粗犷奔放，颇具西域特色。该扁壶的造型特征具有十分明显的游牧民族日常生活的烙印。腹部正反两面都有圈足，背面的起放置平稳之作用，正面的有对称和加固胎体的作用，器侧有两耳便于穿绳携带。小扁壶体态轻盈，可随身携带。扁壶的生产数量很大，器形适合游牧民族使用，为西夏境外的其他窑址所不见，应是西夏瓷酒器的典型器物。然而，西夏人尚武好战，烽火连年。这件扁壶被发掘出来的时候，已破碎为多块，后经修补方得以复原。它的残破，或许就隐含着一段悲壮的历史故事。','此壶1986年出土于宁夏灵武窑址，直口，台唇，长颈，壶体扁圆，圜底位于腹之两侧。','2023-03-27 08:22:53','2023-03-27 08:22:53',0),(33,'黑釉剔花鸟纹大罐','大罐','夏','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end/575ff40be3a04177b97564eb1b6acc61.png','罐为圈足圆腹，束颈唇口，形状壮硕。罐身通体施黑。大罐刻花分三层，第一层为缠枝花围绕肩部一周；罐身为大朵缠枝莲及飞鸟牡丹；下层为仰莲瓣纹','高38.6cm。','2023-03-27 08:24:47','2023-03-27 08:24:47',0),(34,'夏代灰陶器','陶器','夏','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end/d8d3c1db48f54800b933329befc128e6.png','这时期灰陶基本承袭了龙山文化晚期的陶器发展而来。常见的陶质炊器主要有鼎、罐、甑。陶质食器有豆、簋、钵、三足盘。陶质盛器主要有瓮、盆、缸。陶器表面纹饰，除部分食器和盛器为素面磨光，或在磨光面拍印回纹、叶脉纹、涡旋纹、云雷纹、圆圈纹、花瓣纹等图案外，绝大部分陶器表面饰印篮纹、方格纹与绳纹。','','2023-03-27 08:26:35','2023-03-27 08:26:35',0),(35,'夏代黑陶器','陶器','夏','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end/0182e190bdc7462a86d09f1b0ba7de8d.png','夏代黑陶以山东地区烧得最精，陶器轻巧，色泽黑亮，堪与漆器相媲美。黑陶器在夏代的二里头文化时期达于极至，作为珍贵的器物随葬在贵族的墓葬。后由于青铜器的兴起，黑陶器便趋于衰落。','夏代黑陶多为细泥质磨光黑陶，是使用轮制制陶技术制作烧成后，再用毛皮等材料对陶器表面进行打磨抛光，因此整个器表漆黑发亮。','2023-03-27 08:28:57','2023-03-27 08:28:57',0),(36,'白陶刻纹豆','纹豆','商','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end/b0ec398626ff437f93f07e12999362c4.png','商代早期的白陶器形制除鬶、盉外，出现了爵。爵的鋬上和口部多刻划人字形纹饰。商代中期的白陶器出鬶、盉、爵外，还有豆、罐、钵等。商代后期是我国白陶器的高度发展时期，这时的常见器形有罍、壶、双鼻卣、盂、簋等，其胎质纯净洁白细腻，表面多雕刻饕餮纹、夔纹、云雷纹和曲折纹等精美图案。从这些白陶器的形制和纹饰看，都是对同期青铜礼器的仿制，这说明我国白陶的烧制工艺技术已发展到了顶峰时期。','器物表面多为素面磨光，只有少量饰印绳纹。','2023-03-27 08:38:12','2023-03-27 08:38:12',0),(37,'原始瓷青釉弦纹罐','纹罐','商','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end/beefe59f858a4ce8a7cf05f318cd958d.png','原始青瓷出现于约3000年前的商代，历经西周、春秋、战国、汉代，它是从陶向瓷过渡时期的产物，系从印纹硬陶发展而来。其制法是用瓷石制胎，器表施石灰釉，经1200℃高温焙烧而成。胎体烧结后呈灰白或褐色，器表有玻璃质釉层。胎体吸水率低，敲击时能发出清脆的声音。原始青瓷上的釉都呈青色，这是由于釉中含有适量氧化铁所致。原始青瓷最初发现于河南郑州二里岗下层文化遗址与湖北黄陂盘龙城商代中期墓中。河南安阳殷墟、辉县琉璃阁，山东济南大辛庄，河北藁（gǎo 音搞）城及江西清江吴城等商代晚期遗...','高31.4厘米，口径20厘米，底径9.3厘米。罐口外折，短颈，溜肩，肩以下内收，平底。肩与腹部的过度有明显的折角。口内外饰弦纹，其中肩部饰凸起弦纹及锯齿纹共10道。表面施青釉，釉层薄而不匀。','2023-03-27 08:40:49','2023-03-27 08:40:49',0),(38,'商代红陶制品','红陶','商','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end/8a016bf2af2e44bfa8fc8dd2bbeeaf3d.png','在江南地区新出现一种比馒头窑更为先进的陶窑--龙窑。近年来考古工作者在浙江上虞，江西吴城均发现了商代龙窑。这种窑一般依山势建在山坡上，窑身呈长条形倾斜砌筑，外观上形似一条龙从下而上，故名龙窑。龙窑比起横穴窑、竖穴窑、馒头窑来，有多种优点：因依山而建呈倾斜向上状窑炉本身就有自然抽力，窑炉火势大，通风力强，升温快：可据生产需要和技术条件，增加窑的长度，从而提高窑的装烧量，还比较容易维持窑内的窑炉气氛（这对烧瓷器更重要，下面我们再详讲），这就使商代陶器有很大发展，并在商代中期开始了我国由陶到瓷的过渡，诞生了我国最早的瓷器--原始青釉瓷器。','商代陶器在烧成工艺上有很大提高，馒头窑是主要的窑型。','2023-03-27 08:42:35','2023-03-27 08:42:35',0),(39,'商朝早期陶器','陶器','商','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end/52b0bc86fd9f4fd59dbc5105c70c4b67.png','主要器形有：炊器类的鼎、罐、甑、鬲。鬲逐代替鼎而成重要炊器。饮器类的觚、爵，食器类的豆、簋、三足盘，盛器类的瓮、盆、大口尊缸，等等。大口尊、圈足盘、簋 是新出现器形。这时期陶器纹饰以印痕较深的绳纹为主，约占五分之四强。另有少量磨光素面，及磨光面上拍印的云雷纹、双钩纹、圆圈纹，附加堆纹的运用已较前大为减少','以泥质灰陶为主，夹砂灰陶较夏代为多，有少量红陶、棕陶和白陶，而黑陶、黑衣陶已很少见。','2023-03-27 08:44:13','2023-03-27 08:44:13',0),(40,'中期陶器','陶器','商','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end/c1b6060f256841188784495c6168eb57.png','以泥质灰陶和夹砂灰陶最多，约占同期陶器总数的90%以上，另有一些夹砂粗红陶、泥质黑皮陶、泥质红陶，白陶的数量占陶器总量的比例仍很小，但较商代早期有所增加。器形主要有：作炊器的鬲、罐、甑，作饮器的爵、觚、杯，作食器用的簋、豆、钵、鼎、作盛器的盆、瓮、大口尊、罐、壶，等等。在制作精细的簋、豆、盆、罐、壶、瓮的腹部、肩部、圈足上，常有由图案纹饰组成的带条，主要纹饰有：饕餮纹、夔纹、方格纹、人字纹、花瓣纹、云雷纹、涡漩纹、曲折纹、连环纹、乳钉纹、蝌蚪纹、圆圈纹、火焰纹等。其中以饕餮纹组成的带条最多，一般是三组饕餮纹构成一个条带。饕餮纹在陶器上大量拍印，仅在商代中期最为盛行，到商代后期就很少见到了。','在造型上，商代中期陶器口部折沿基本不见，多为卷沿，底部主要是圜底和袋状足，圈足器增多，平底器大为减少。商代中期陶器的纹饰，绳纹所占比重更大，几达98%。','2023-03-27 08:45:43','2023-03-27 08:45:43',0),(41,'晚期陶器','陶器','商','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end/783b4524f2ce452a8870e2fd015f17de.png','仍以泥质灰陶和夹砂灰陶为最多，另有少量的泥质红陶特别值得注意的是，商代晚期我国白陶得到了高度发展，成为当时占陶器中比例不大却十分名贵、重要的一个陶器品种，下面我们还要单独谈几句。商代晚期陶器的器形，作炊器的主要有爵、觚、甑，作炊器的鬲、罐、甑，作饮器的爵、觚、杯，作食器用的簋、豆、钵、鼎、作盛器的盆、瓮、大口尊、罐、壶，等等。纹饰仍以绳纹为主，另有一些刻划纹、凹线纹、弦纹、附加堆纹、镂孔，等等。商代中期盛行的饕餮纹、云雷纹、方格纹等带条状精美图案纹饰，这时期陶器上已很少见到，这一点是我们区分商代中期与晚期陶器的参考之一。在造型上，商代晚期陶器中的平底器，圈足器较前一时期明显增多，袋状足也不少，而圜底器则有所减少。','','2023-03-27 08:47:09','2023-03-27 08:47:09',0),(42,'灰陶双耳带盖簋','簋','周','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end/81ecdf7f983047c5a0104635aa5d2344.png','周代灰陶是承袭陕西一带的先周陶器和吸收商代后期的一些陶器形制发展而来的。这时期的灰陶器以泥质灰陶和夹砂灰陶最多，但也有夹砂红陶和泥质红陶。炊器主要有鬲、甗、甑；食器主要是豆和簋；盛器主要有罍、罐、瓮、盆、盂等。这时期陶器的装饰纹样基本都是纹理较粗的绳纹，还有一些划线纹、篦纹、弦纹和刻划三角纹等.','','2023-03-27 08:48:28','2023-03-27 08:48:28',0),(43,'网格纹硬陶瓿','陶瓿','周','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end/8935da4b29df4fba9ac20c1e4c9b3588.png','周代，尤其是西周时期，是印纹硬陶发展兴盛的时期，可能因为黄河中下游地区青铜器和原始瓷器使用较多，故这时期的印纹硬陶器主要盛行在长江中下游地区。从考古发掘看，长江以南特别是东南沿海地区，印纹硬陶出土数量较多，延续时间长，从夏直到秦汉。在黄河流域地区，印纹硬陶出土数量既少，出现时间也晚。印纹硬陶器基本上以泥条盘筑法成型，器物的一些附件如鼻、耳等，则为手制后粘接在器体上，其纹饰多采用拍印法。','西周时期常见的器形有瓮、坛、瓿等，其中瓮和坛的腹部丰满，器形高大。纹饰有云雷纹、方格纹、回纹、曲折纹、菱形纹、波浪纹和夔纹等，纹饰制作工整，且出现了两种或三种纹饰复合使用的现象。','2023-03-27 08:49:43','2023-03-27 08:51:45',0),(44,'原始青瓷鼎','鼎','周','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end/4b99656d9373466fbe42190a164c7396.png','周代，尤其是西周时期的原始瓷器比商代有较大的发展。周代原始瓷器的发现更加普遍，在北京琉璃河、河南洛阳、陕西长安和宝鸡、甘肃灵台、安徽屯溪、江苏句容等地都有发现。而屯溪、句容等地的西周墓中，大量出土原始瓷器，器形也较丰富（见屯溪西周墓），这也说明明南方的原始瓷器远比北方发达。','这一时期的原始瓷器仍大都为生活用的器皿，器形有尊、罐、瓮、豆、碗、盂等。纹饰有弦纹、方格纹、曲折纹等，双乳钉或钮状的附加装饰比较盛行。','2023-03-27 08:51:04','2023-03-27 08:51:04',0),(45,'三角纹圆底彩陶罐','罐','周','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end/a8a5c74b52f24e31ab4c77c47bdc3ac2.png','因首次发现于甘肃民勤沙井而得名。以双耳及单耳罐较多，纹饰有直线、交叉、三角、菱形等，常施于颈腹部。','','2023-03-27 08:52:53','2023-03-27 08:53:10',0),(46,'神人纹犬纹双耳罐','罐','周','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end/6275524383ae424f90adf21896d31072.png','因首先发现与甘肃临洮辛店而得名，分布于洮河、大夏河、湟水以及黄河沿岸部分地区。陶器以夹砂粗红陶为主，并有大量彩陶。彩陶表面磨光，多用黑色绘画花纹。','','2023-03-27 08:54:21','2023-03-27 08:54:21',0),(47,'灰陶鸟柱形灯','灯','春秋战国','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end%2F%E5%9B%BE%E7%89%8717.png','春秋陶器以泥质陶为主，夹砂灰陶次之，另有少量夹砂红陶和夹砂棕陶。常见器形，作炊器的主要是鬲、釜、甑，作食器的主要有豆、盂、盘，作盛器主要有瓮、盆、罐。大约从商代晚期开始出现的专用于随葬的陶明器，春秋时期有较大的发展，有仿青铜礼器的鼎、 盘，也有仿日用器的鬲、罐、豆、盂，等等，陶器的造型以平底器和三足器为主，有少量圈足器。春秋陶器纹饰更为简单，主要是粗绳纹、瓦旋纹。可以看出，春秋时期陶器不仅品种减少，而且纹饰单调，大约是因为当时的陶器手工业的主要力量，用于生产和发展更为先进适用的原始瓷。','春秋时期的灰陶器以平底器和袋状三足器为主，兼有少量圈足器。质料以泥质灰陶为主，夹砂灰陶次之。常见器形有鬲、釜、甑、豆、盂、盘、瓮、盆、罐等，其中以炊器中的鬲为最多。这时期陶器纹饰比西周简单，主要饰印粗绳纹和瓦旋纹。','2023-03-27 09:10:11','2023-03-27 09:10:11',0),(48,'印纹陶釜','陶釜','春秋战国','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end%2F%E5%9B%BE%E7%89%8718.png','春秋时期印纹硬陶主要盛行于长江中下游地区，基本承袭西周时期印纹硬陶而发展，与西周时期的基本相同。常见器形有瓮、坛、瓿、盆、釜等','胎质仍以紫褐色、红褐色、灰褐色为主；器表拍印几何形图案，出西周常见纹饰外，还有大方格纹和布纹。','2023-03-27 09:13:35','2023-03-27 09:13:35',0),(49,'原始青瓷出戟尊','尊','春秋战国','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end%2F%E5%9B%BE%E7%89%8719.png','原始青瓷发展到了春秋时期，器类丰富程度超过以往，仿青铜器的原始青瓷盛行，制作和烧造等技术发展到顶峰，造型和装饰纹样变得更加多样化和世俗化。','这一时期的原始青瓷大多仿自青铜礼器，这是因为这一时期以宗法等级及礼乐制度为核心的商周用鼎制度遭到破坏，原来的“天子九鼎、诸侯七、大夫五、元士三也”的“列鼎制”，往往变成诸侯陪葬九鼎、大夫用七鼎。而用不起铜鼎礼器的，往往用原始瓷器替代。','2023-03-27 11:13:25','2023-03-27 11:13:25',0),(50,'彩绘三角纹灰陶鼎','陶鼎','春秋战国','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end%2F%E5%9B%BE%E7%89%8720.png','常见器物有釜、甑、罐、壶、盆、钵、瓮、碗、豆、杯等。在河南登封还发现了陶量，这是研究战国时期度量制度的重要资料。陶质生产工具种类不多，有纺轮和制陶用的拍子、印模、陶范等。泥质灰陶多数用轮制成形，也有用模制和手制的。这时期大型器物的烧成表明当时制陶技术的进步，使后来秦代兵马俑的制作成为可能。','战国时期各地都广泛使用的还是灰陶，包括日用器具、生产工具、陶塑和陶明器等。日用陶器主要是泥质灰陶，只有釜之类的炊器为夹砂陶。','2023-03-27 11:15:09','2023-03-27 11:15:09',0),(51,'黑陶鸟驻盘','盘','春秋战国','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end%2F%E5%9B%BE%E7%89%8720.png','中山黑陶不以彩绘突出，而以磨光与压划纹相配合，完全靠线条组合成装饰图案，古雅别致。虽然压划纹的种类不是很多，但均为当时流行的一些纹饰，并且线条流畅，各种花纹之间巧妙地进行不同形式的组合，极富变化。如填横线纹的卷云纹、填波折纹的三角纹等，形成独特的风格，显得非常典雅。又如磨光处加凹纹或凸弦纹，既起到分隔作用又有装饰作用战国时期的黑陶由于受到当时漆器工艺的影响，大多采用磨光、暗花、硃绘、粉绘等装饰方法，呈现出类似错金、银的黑漆器，轻盈光亮，古雅别致。而战国时期的黑陶以中山黑陶见长。中山黑陶在造型上，盖、钮、器耳、器足等均用模制，动物形象如飞鸟、鸭首等为雕琢成形。主件、附件分别制成后再粘合成一体，这样制作的器形线条挺拔优美，转折处干净洗练。中山黑陶不以彩绘突出，而以磨光与压划纹相配合，完全靠线条组合成装饰图案，古雅别致。虽然压划纹的种类不是很多，但均为当时流行的一些纹饰，并且线条流畅，各种花纹之间巧妙地进行不同形式的组合，极富变化。如填横线纹的卷云纹、填波折纹的三角纹等，形成独特的风格，显得非常典雅。又如磨光处加凹纹或凸弦纹，既起到分隔作用又有装饰作用。','战国时期的黑陶由于受到当时漆器工艺的影响，大多采用磨光、暗花、硃绘、粉绘等装饰方法，呈现出类似错金、银的黑漆器，轻盈光亮，古雅别致。而战国时期的黑陶以中山黑陶见长。中山黑陶在造型上，盖、钮、器耳、器足等均用模制，动物形象如飞鸟、鸭首等为雕琢成形。主件、附件分别制成后再粘合成一体，这样制作的器形线条挺拔优美，转折处干净洗练。','2023-03-27 11:16:22','2023-03-27 11:24:26',1),(52,'黑陶鸟驻盘','盘','春秋战国','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end%2F%E5%9B%BE%E7%89%8721.png','中山黑陶不以彩绘突出，而以磨光与压划纹相配合，完全靠线条组合成装饰图案，古雅别致。虽然压划纹的种类不是很多，但均为当时流行的一些纹饰，并且线条流畅，各种花纹之间巧妙地进行不同形式的组合，极富变化。如填横线纹的卷云纹、填波折纹的三角纹等，形成独特的风格，显得非常典雅。又如磨光处加凹纹或凸弦纹，既起到分隔作用又有装饰作用战国时期的黑陶由于受到当时漆器工艺的影响，大多采用磨光、暗花、硃绘、粉绘等装饰方法，呈现出类似错金、银的黑漆器，轻盈光亮，古雅别致。而战国时期的黑陶以中山黑陶见长。中山黑陶在造型上，盖、钮、器耳、器足等均用模制，动物形象如飞鸟、鸭首等为雕琢成形。主件、附件分别制成后再粘合成一体，这样制作的器形线条挺拔优美，转折处干净洗练。中山黑陶不以彩绘突出，而以磨光与压划纹相配合，完全靠线条组合成装饰图案，古雅别致。虽然压划纹的种类不是很多，但均为当时流行的一些纹饰，并且线条流畅，各种花纹之间巧妙地进行不同形式的组合，极富变化。如填横线纹的卷云纹、填波折纹的三角纹等，形成独特的风格，显得非常典雅。又如磨光处加凹纹或凸弦纹，既起到分隔作用又有装饰作用。','战国时期的黑陶由于受到当时漆器工艺的影响，大多采用磨光、暗花、硃绘、粉绘等装饰方法，呈现出类似错金、银的黑漆器，轻盈光亮，古雅别致。而战国时期的黑陶以中山黑陶见长。中山黑陶在造型上，盖、钮、器耳、器足等均用模制，动物形象如飞鸟、鸭首等为雕琢成形。主件、附件分别制成后再粘合成一体，这样制作的器形线条挺拔优美，转折处干净洗练。','2023-03-27 11:16:42','2023-03-27 11:16:42',0),(53,'三彩仙人骑龙凤首壶','壶','唐','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end%2F%E5%9B%BE%E7%89%8722.png','唐三彩是一种低温釉陶，在色釉中加入不同的金属氧化物，经过焙烧便形成浅黄，赭黄，浅绿，深绿，天蓝，茄紫，褐红等多种色彩，但多以黄，褐，绿三色为主，以天蓝，茄紫较珍贵稀见。它主要是陶胚上涂彩釉，在烘制过程中发生化学变化，使色釉融溶互相浸润其色彩浓淡变化，斑驳淋漓。使三彩器因色彩流畅协调，造型新颖多彩的相互辉映下富涵堂皇贵丽的艺术魅力，是具有中国独特风格的传统工艺精品。','壶身扁圆，以凤首为流，洗口，束颈弧腹，于洗口和腹部间贴连一六瓣花朵为把，壶身纹饰以白地开光珍珠地模印一仙人骑龙为主纹，白黄绿三色相间。此种纹饰非常稀见，除反映唐文化的浪漫主义和求仙思想外，更是不可多得的艺术珍品，可参阅日本松岗美术馆有件凤首壶，可兹比较。','2023-03-27 11:26:12','2023-03-27 11:26:12',0),(54,'郏县窑黑釉月白斑双系罐','罐','唐','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end%2F%E5%9B%BE%E7%89%8723.png','底釉有黑釉、酱釉、天蓝釉等。釉上所饰斑纹有月白、天蓝、灰黑等色，有的两种颜色互相交融流淌。一般来说，深色釉上饰浅色斑纹，而浅色釉上饰深色斑纹，斑纹或任意泼洒，或自然流淌，釉在高温烧制过程中变化莫测，呈现出色彩斑斓的装饰效果。','高17厘米，口径9.7厘米，足径10.3厘米。此罐口部外撇，短颈，圆腹，平底。肩部对称置半环形系。罐身施黑褐釉至近足部，上饰月白色斑块。此为唐代著名的花瓷罐。在唐代花瓷中以罐最为常见，有高体的、矮体的、口大的、口小的，造型变化丰富。','2023-03-27 11:27:17','2023-03-27 11:27:17',0),(55,'长沙窑褐釉模印贴花双耳罐','罐','唐','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end%2F%E5%9B%BE%E7%89%8724.png','唐代长沙窑器物以日用瓷为主，除满足国内需要外，还大量外销。其造型简洁朴素，讲究细微之处的变化，以各种形式的罐和罐最多见。早期装饰上的用彩多在模印贴花的人物、狮、葡萄上涂褐色或绿色大彩斑，继而发展为用单一的褐彩或绿彩绘画，以后再逐步发展到用褐、绿双色彩绘。唐代长沙窑是中国大量生产釉下彩瓷器的发源地，它开创了瓷器彩绘装饰的新途径。','高19.2厘米，口径12.6厘米，足径14.2厘米。罐唇口，直颈，丰肩，圆腹，腹下敛，平底。胎呈灰白色，器身施褐中泛黄色釉。肩部有对称方形双耳及四朵模印贴花纹。近足处一周及底部素胎无釉。','2023-03-27 11:28:38','2023-03-27 11:28:38',0),(56,'白釉双龙耳瓶','瓶','唐','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end%2F%E5%9B%BE%E7%89%8725.png','以造型的空间虚实以及轮廓线的自然和谐见长，对称的龙形柄与口、肩相连所形成的虚实对比，于浑厚质朴中洋溢出俊俏与妩媚，圆滑的曲线与折角处分明的棱角有机结合后，又于刚健挺拔中透出协调柔和的韵律美。器身施白釉，更显高贵典雅。这种瓶的形制受到了外来文化的影响，在融进本民族的文化特色后，端庄典雅的形体一展大唐盛世的神韵。双龙耳瓶是唐代流行的瓶式，除白釉外还有青釉、唐三彩等品种。','瓶盘口，细长颈，溜肩，腹部丰满，至胫处渐收。颈上突起5道弦纹。口沿与肩之间有两个对称的龙形柄高耸直立，龙头探进瓶口衔住口沿。胎极白，施半截白釉。','2023-03-27 11:30:55','2023-03-27 11:30:55',0),(58,'唐三彩贴花三足炉','瓷器','唐','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end%2F%E5%9B%BE%E7%89%8726.png','唐三彩是造型工艺和釉色装饰的结合。有一类唐三彩三足炉，在釉色装饰的基础上，还采用了胎上帖花的方法。贴花是在器胎上粘贴模印制作的浮雕图案纹饰，然后整体施以彩釉统一烧制，这种贴花工艺更加凸显了其立体装饰的效果。肩部一周等距离装饰五个叶状贴花。腹上部略下则间饰五个狮形贴花，呈浮雕状，幼狮模样，圆眼竖耳，大嘴怒张，翘尾稍卷，昂首奔腾，额、鬓、颈、胸、腿、尾等处均有长短各异的体毛，造型夸张，动感强烈。器施酱黄、墨绿、乳白釉，色彩斑斓，相互交融，错落有致，层次分明，给人一种美的质感。釉不及底，露出陶胎，质地洁白，细密坚致。三足炉是巩义黄冶窑常见的器形，其造型仿自唐代的金银器，以唐三彩较为多见，此外还有白釉、黑釉、蓝釉等品种。据有关专家研究成果表明，这件唐三彩三足炉亦应是巩义黄冶窑之制品。','口径12.5厘米，腹径21.3厘米，高17.1厘米，侈口外翻，卷沿圆唇，短颈微束，丰肩略斜，圆鼓腹，圜形底，腹下部位设有兽爪状三足。','2023-03-27 11:52:21','2023-03-27 11:52:21',0),(59,'青釉凤首龙柄壶 ','瓷器','唐','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end%2F%E5%9B%BE%E7%89%8727.png','壶盖与壶口吻合成凤头状，使得壶整体颇似一只挺立的凤鸟。壶柄塑成一直立的蟠龙，龙口衔住口沿，作窥视探饮状，前肢撑于壶肩部，后肢立于喇叭形底座上。壶体以塑贴和刻划技法装饰。腹部塑贴主体纹饰两层，上为6个联珠纹圆形开光，内有手舞足蹈的力士，下为宝相花6朵。口沿、颈、肩及胫部饰以联珠纹、莲瓣、卷叶或垂叶纹。各组纹饰间以弦纹相隔。此壶胎体厚重，釉层凝厚，玻璃质感强，带有北朝以来北方青瓷的遗风。其装饰纹样繁复，结构严谨，层次清晰。六朝至唐代，我国与西亚各国文化交流频繁，波斯的一种鸟首壶传到我国，影响所及，在唐代的青瓷、白瓷及三彩釉陶中出现了凤首壶，其中以这件青釉凤首龙柄壶最为精美。此壶既吸取了波斯萨珊王朝（226-642年）金银器造型的特点，又融入了我国传统的龙凤装饰艺术，集塑贴、模印、刻花、划花等装饰技法于一体，体现了唐代制瓷工匠的高超技艺。','通高41.3cm，口径19.3cm，足径10.2cm。','2023-03-27 11:56:50','2023-03-27 11:56:50',0),(60,'唐花瓷双系罐','瓷器','唐','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end%2F%E5%9B%BE%E7%89%8728.png','-罐口微外撇，短颈，颈两侧对称各置一系，丰肩，肩以下渐收，平底。近底处刻深弦纹一道。通体施黑釉，近底处露胎。乌黑的釉面上装饰四块灰蓝色斑块，似色料自然流淌而成，妙趣天成。','高19.9cm，口径7.4cm，底径10.7cm。','2023-03-27 11:57:59','2023-03-27 11:57:59',0),(61,'白釉奓斗 ','瓷器','唐','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end%2F%E5%9B%BE%E7%89%8729.png','奓斗口外撇，口沿呈漏斗形碗状，扁圆腹，平底。器里外施白釉，底无釉。此件奓斗胎质洁白细腻，釉色莹润雪白，造型规整大方，在唐代白瓷中属上乘之作。','高9.9cm，口径12cm，足径6.9cm。','2023-03-27 11:58:42','2023-03-27 11:58:42',0),(62,'哥窑小口罐 ','瓷器','元','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end%2F%E5%9B%BE%E7%89%8730.png','唇口，短颈，丰肩，肩以下渐收。底为圈足，通体施月白色釉。开金丝铁线纹片，胎呈紫黑色。罐在宋元哥窑中较为稀少，此罐虽为小器，但十分难得。“哥窑”名列宋代五大名窑，在陶瓷史上有举足轻重的地位。哥窑胎多紫黑色、铁黑色、也有黄褐色。釉为失透的乳浊釉，釉面泛一层酥光，釉色以炒米黄、灰青多见，釉面大小纹片结合。经染色后大纹片呈深褐色，小纹片为黄褐色，也称‘金丝铁线’“墨纹梅花片”“叶脉纹”‘文武片’等。这是传世哥窑的主要特征之一。器形有各式瓶、炉、尊、洗及碗、盆、碟等。多见仿古造型，底足制作不十分规整，釉面常见缩釉和棕眼。','高7×直径2.7×F8.5cm','2023-03-27 12:00:55','2023-03-27 12:00:55',0),(63,'元钧窑天青釉紫斑双系瓷罐','瓷器','元','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end%2F%E5%9B%BE%E7%89%8731.png','1974年龙堌公社奚楼出土。该罐直口，圆唇，鼓腹，宽圈足。口沿与肩部饰对称双系。器身里外施青釉，施釉不到底。肩、腹部各有一处窑变现象，呈玫瑰紫色，该器造型疏朗，釉彩莹润，窑变艳丽，极具观赏与研究价值。','高16.8厘米。','2023-03-27 12:01:29','2023-03-27 12:01:29',0),(64,'元釉里红玉壶春瓶','瓷器','元','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end%2F%E5%9B%BE%E7%89%8732.png','1975年 苏坑元代墓出土。整体呈水滴状。胎白，质细，釉色闪青。颈下饰三组对称卷云纹，腹部以红色饰对称缠枝花卉。颈、腹上下部各饰三组细纹带。该瓶造型秀丽，红色纯正，充分反映了元代匠人高超的烧制技艺。','高28.1厘米。','2023-03-27 12:02:47','2023-03-27 12:02:47',0),(65,'元钧窑蓝釉双系瓷罐','瓷器','元','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end%2F%E5%9B%BE%E7%89%8733.png','1952年龙堌公社奚楼村出土。该器直口，圆唇，鼓腹，宽圈足。口沿与肩部饰对称双系。器身里外施天蓝釉，施釉不到底，圈足露胎。肩部有一处窑变现象，呈火石红色。该器造型疏朗，釉色莹润。','高16.2厘米。','2023-03-27 12:03:35','2023-03-27 12:03:35',0),(66,'草叶纹白瓷罐','瓷器','元','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end%2F%E5%9B%BE%E7%89%8734.png','直口，短颈，丰肩，上腹鼓，至下渐收，圈足 。胎质坚硬，釉色轻薄呈白色，下腹至底无釉。肩部饰草叶纹，线条流畅，展示出自然天成之美。','该罐器口外径16.7厘米，腹围82.6厘米。','2023-03-27 12:04:41','2023-03-27 12:04:41',0),(67,'寿字鹤鹿纹葫芦瓶','瓷器','明','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end%2F%E5%9B%BE%E7%89%8735.png','葫芦瓶，上截草书二寿字，饰以梅、竹、灵芝及仙鹤二只。腰部画转枝桃实四枚；下截腹上画松、竹梅、灵芝，加饰鹤、鹿各二只。仔细观察寿字的笔法，双勾的线条转折起伏，与瓶上的梅枝、竹枝、桃枝的画法都有类似之处，意味寿字是由一种藤蔓或树枝结字而成。雕树则是明代晚期盆景或园林常出现的景观。根据《江西省大志》纪录，当时皇家下令景德镇御窑厂烧造一种装饰“万岁藤”的青花瓷器，应该就是这类作品的写照。而将雕树描绘在瓷器上则为嘉靖时期的创举。学界也认为，灵石长出万寿藤，应该是为满足嘉靖皇帝求仙的渴望。','','2023-03-27 12:06:11','2023-03-27 12:06:11',0),(68,'青花缠枝花卉纹碗','瓷器','明','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end%2F%E5%9B%BE%E7%89%8736.png','碗直口，深弧壁，圈足。内外青花装饰，内底一周回纹内绘折枝牡丹纹，内壁绘缠枝菊纹，口沿内外分别描绘浪花纹和缠枝灵芝纹，外壁绘缠枝牡丹纹，近足处绘变形莲瓣纹，莲瓣内绘朵花纹，圈足外墙绘回纹。圈足内施白釉。无款识。明初洪武时的青花瓷器器型大都延续元代风貌，此器就与元代枢府釉碗造型接近，但又有向后来以秀美取胜的永乐碗形演变的趋势。此碗造型硕大规整，线条比元代碗形更显圆润，所绘纹饰笔法娴熟，线条流畅。','高16.5厘米，口径40.5厘米，足径23厘米。','2023-03-27 12:07:08','2023-03-27 12:07:08',0),(69,'红绿彩 天马纹盖罐','瓷器','明','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end%2F%E5%9B%BE%E7%89%8737.png','平顶盖罐，盖面彩绘六朵花，肩上画缠枝花纹；器腹四匹红、黄色的天马踏浪而行，穿梭在云间。底青花书“大明嘉靖年制”六字楷款。红绿彩瓷，主要由红、黄、绿三种颜色组成，也常配紫色、孔雀蓝与黑彩。釉色用毛笔醮取釉料在已烧成的高温白瓷上作画，先以红料描绘纹饰的轮廓，再平涂花纹，约800°C烘烤而成。明代宫廷烧造瓷器，先制定式样与数量，再由景德镇官窑照样制作。嘉靖彩瓷红、绿、黄、紫诸色互见，部分大片浓烈对比，部分细琐五彩缤纷，数量之多，前世罕见，引领彩瓷走向世俗艳丽的风貌。嘉靖为彰显皇位来自成化嫡系的印象，所造五彩瓷也大量仿制成化样式，成品虽不似其文雅清秀，却更为繁富华丽，自成风格。','','2023-03-27 12:07:48','2023-03-27 12:07:48',0),(70,'明“大吉利”白釉瓷瓶','瓷器','明','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end%2F%E5%9B%BE%E7%89%8738.png','该瓶厚唇，敞口，短颈，鼓腹至下部渐收成圈足微外撇，整体修长，上部施白釉，有开片；下部至足无釉露胎。肩部饰黑褐色纹带一周，腹部以黑褐色书“大吉利”三字。器物造型修长，笔意生动，率真。','高17.2厘米，口径4.9厘米。1978年征集。','2023-03-27 12:08:26','2023-03-27 12:08:26',0),(71,'明青花鹊登枝碗','瓷器','明','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end%2F%E5%9B%BE%E7%89%8739.png','碗口沿外撇，浅青釉，腹渐收，圈足。口沿里外各饰线纹一周。碗底内绘草叶纹；腹外壁饰两组对称喜鹊登枝图案，圈足外部饰线纹一周。纹饰简练，笔意生动。','高4.3厘米。','2023-03-27 12:09:36','2023-03-27 12:09:36',0),(72,'黄釉 撇口碗','瓷器','明','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end%2F%E5%9B%BE%E7%89%8740.png','黄色象征大地，也是稻谷熟成时的色彩，因此，祭祀地祇、农耕、蚕桑之神也用黄釉祭器。据阴阳五行的色彩学说，黄色居天下之中，犹如君主坐镇国家中央，掌管四方。因此，黄色也成为皇室专属，是官民不得僭越的颜色。黄釉深碗，厚胎，内外施娇黄釉，釉色分布不均，底青花书“大明嘉靖年制”六字楷款。明代简化祭器的造型，使用日常型制的碗、盘、罐等器皿。嘉靖时期祭器“登”以大碗代之，是祭祀时盛放羹汤的器皿。本件黄釉大碗厚重罕见，推测应该是祭器“登”。','','2023-03-27 12:10:25','2023-03-27 12:10:25',0),(73,'明褐釉双系瓷','瓷器','明','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end%2F%E5%9B%BE%E7%89%8741.png','轮制。直口，溜肩，腹部逐渐下收，矮圈足。肩部饰对称双系。口沿至腹中部及罐里施深褐釉，腹下部至圈足无釉。','高14.8厘米，腹围43.2厘米。','2023-03-27 12:11:12','2023-03-27 12:11:12',0),(74,'磁州窑花卉纹玉壶春瓶','瓷器','金','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end%2F%E5%9B%BE%E7%89%8742.png','磁州窑在我国瓷业的作用，明代已有评估。瓷器，又名磁器；《五杂俎》解释“磁器”是这样说的：“今俗语窑器谓之‘磁器’，盖河南磁州窑最多，故相沿名之。”白地黑彩乃磁州窑最具代表之纹饰风格，冠绝各类磁州窑名品，其黑白分明之雅韵，备受藏界的追捧。磁州窑是中国北方最庞大的民窑体系，蜚声中外，深深影响中国的陶瓷生产的发展。','高18.5cm','2023-03-27 12:16:04','2023-03-27 12:16:04',0),(75,'浅棕色釉云纹罐','瓷器','金','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end%2F%E5%9B%BE%E7%89%8743.png','直口，广肩，腹下部渐收，矮圈足微外撇。腹上部至口沿施浅棕色釉，有小开片。口沿施不规则线纹一周，肩部饰五组卷云纹。腹下部无釉露胎，胎质细腻，呈白色。','罐高19.8厘米，口径14.9厘米，腹围74.2厘米。','2023-03-27 12:16:44','2023-03-27 12:16:44',0),(76,'磁州窑白地黑花龙凤纹罐','瓷器','金','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end%2F%E5%9B%BE%E7%89%8744.png','罐直口，短颈，丰肩，肩以下渐收敛，圈足。外壁通体白地黑花装饰。其做法是在已成型的坯胎上先施一层洁白的化妆土遮盖住含杂质较多的坯体，然后用毛笔蘸“斑花石”彩料在化妆土上描绘纹饰，肩部绘缠枝菊纹，肩以下至足部描绘龙、凤纹。最后施透明釉，入窑经高温一次烧成。','高28厘米，口径16.5厘米，足径13厘米。','2023-03-27 12:17:36','2023-03-27 12:17:36',0),(77,'哥窑米黄釉双耳瓶','瓷器','金','https://demo-1311835527.cos.ap-beijing.myqcloud.com/back_end%2F%E5%9B%BE%E7%89%8745.png','清·仁和人许之衡所撰《饮流斋说瓷》记载 ：“谓口际有边，深黄而近紫，足则铁色也。宋瓷多数有之，哥窑尤夥。其始质细，性坚，其体重，多断纹，隐裂如鱼子，亦有大小碎块文，即开片也。釉以米色、豆绿二种居多，有紫口铁足。无釉之处所呈之色，其红如瓦屑。其釉极厚润纯粹，历千年而莹泽如新。人为开片，始于宋代哥窑，其后因递仿之。”','高23.6cm；口径10.8cm；底径9cm','2023-03-27 12:18:16','2023-03-27 12:18:16',0);

/*Table structure for table `sys_captcha` */

DROP TABLE IF EXISTS `sys_captcha`;

CREATE TABLE `sys_captcha` (
  `uuid` char(36) NOT NULL COMMENT 'uuid',
  `code` varchar(6) NOT NULL COMMENT '验证码',
  `expire_time` datetime DEFAULT NULL COMMENT '过期时间',
  PRIMARY KEY (`uuid`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='系统验证码';

/*Data for the table `sys_captcha` */

/*Table structure for table `sys_config` */

DROP TABLE IF EXISTS `sys_config`;

CREATE TABLE `sys_config` (
  `id` bigint NOT NULL AUTO_INCREMENT,
  `param_key` varchar(50) DEFAULT NULL COMMENT 'key',
  `param_value` varchar(2000) DEFAULT NULL COMMENT 'value',
  `status` tinyint DEFAULT '1' COMMENT '状态   0：隐藏   1：显示',
  `remark` varchar(500) DEFAULT NULL COMMENT '备注',
  PRIMARY KEY (`id`),
  UNIQUE KEY `param_key` (`param_key`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='系统配置信息表';

/*Data for the table `sys_config` */

/*Table structure for table `sys_log` */

DROP TABLE IF EXISTS `sys_log`;

CREATE TABLE `sys_log` (
  `id` bigint NOT NULL AUTO_INCREMENT,
  `username` varchar(50) DEFAULT NULL COMMENT '用户名',
  `operation` varchar(50) DEFAULT NULL COMMENT '用户操作',
  `method` varchar(200) DEFAULT NULL COMMENT '请求方法',
  `params` varchar(5000) DEFAULT NULL COMMENT '请求参数',
  `time` bigint NOT NULL COMMENT '执行时长(毫秒)',
  `ip` varchar(64) DEFAULT NULL COMMENT 'IP地址',
  `create_date` datetime DEFAULT NULL COMMENT '创建时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='系统日志';

/*Data for the table `sys_log` */

/*Table structure for table `sys_menu` */

DROP TABLE IF EXISTS `sys_menu`;

CREATE TABLE `sys_menu` (
  `menu_id` bigint NOT NULL AUTO_INCREMENT,
  `parent_id` bigint DEFAULT NULL COMMENT '父菜单ID，一级菜单为0',
  `name` varchar(50) DEFAULT NULL COMMENT '菜单名称',
  `url` varchar(200) DEFAULT NULL COMMENT '菜单URL',
  `perms` varchar(500) DEFAULT NULL COMMENT '授权(多个用逗号分隔，如：user:list,user:create)',
  `type` int DEFAULT NULL COMMENT '类型   0：目录   1：菜单   2：按钮',
  `icon` varchar(50) DEFAULT NULL COMMENT '菜单图标',
  `order_num` int DEFAULT NULL COMMENT '排序',
  PRIMARY KEY (`menu_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='菜单管理';

/*Data for the table `sys_menu` */

/*Table structure for table `sys_role` */

DROP TABLE IF EXISTS `sys_role`;

CREATE TABLE `sys_role` (
  `role_id` bigint NOT NULL AUTO_INCREMENT,
  `role_name` varchar(100) DEFAULT NULL COMMENT '角色名称',
  `remark` varchar(100) DEFAULT NULL COMMENT '备注',
  `create_user_id` bigint DEFAULT NULL COMMENT '创建者ID',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  PRIMARY KEY (`role_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='角色';

/*Data for the table `sys_role` */

/*Table structure for table `sys_role_menu` */

DROP TABLE IF EXISTS `sys_role_menu`;

CREATE TABLE `sys_role_menu` (
  `id` bigint NOT NULL AUTO_INCREMENT,
  `role_id` bigint DEFAULT NULL COMMENT '角色ID',
  `menu_id` bigint DEFAULT NULL COMMENT '菜单ID',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='角色与菜单对应关系';

/*Data for the table `sys_role_menu` */

/*Table structure for table `sys_user` */

DROP TABLE IF EXISTS `sys_user`;

CREATE TABLE `sys_user` (
  `user_id` bigint NOT NULL AUTO_INCREMENT COMMENT '用户 id',
  `username` varchar(50) NOT NULL COMMENT '用户名',
  `password` varchar(100) DEFAULT NULL COMMENT '密码',
  `salt` varchar(20) DEFAULT NULL COMMENT '盐',
  `email` varchar(100) DEFAULT NULL COMMENT '邮箱',
  `mobile` varchar(100) DEFAULT NULL COMMENT '手机号',
  `status` tinyint DEFAULT NULL COMMENT '状态  0：禁用   1：正常',
  `create_user_id` bigint DEFAULT NULL COMMENT '创建者ID',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  PRIMARY KEY (`user_id`),
  UNIQUE KEY `username` (`username`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='系统用户';

/*Data for the table `sys_user` */

/*Table structure for table `sys_user_role` */

DROP TABLE IF EXISTS `sys_user_role`;

CREATE TABLE `sys_user_role` (
  `id` bigint NOT NULL AUTO_INCREMENT,
  `user_id` bigint DEFAULT NULL COMMENT '用户ID',
  `role_id` bigint DEFAULT NULL COMMENT '角色ID',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='用户与角色对应关系';

/*Data for the table `sys_user_role` */

/*Table structure for table `sys_user_token` */

DROP TABLE IF EXISTS `sys_user_token`;

CREATE TABLE `sys_user_token` (
  `user_id` bigint NOT NULL COMMENT '用户 id',
  `token` varchar(100) NOT NULL COMMENT 'token',
  `identity` int DEFAULT NULL COMMENT '用户身份 null: sys系统用户 | 1: user',
  `expire_time` datetime DEFAULT NULL COMMENT '过期时间',
  `update_time` datetime DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`user_id`),
  UNIQUE KEY `token` (`token`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='系统用户Token';

